import React, {Component} from "react";
import "./ReactCardFlip.css"
import FlipCard from 'react-flipcard';

class ReactCardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            isFlipped: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: '',
            style:''
        };
        this.handleClick = this.handleClick.bind(this);
    }


    render() {

        return(
            <Flippy
                flipOnHover={false} // default false
                flipOnClick={true} // default false
                flipDirection="horizontal" // horizontal or vertical
                ref={(r) => this.flippy = r} // to use toggle method like this.flippy.toggle()
                // if you pass isFlipped prop component will be controlled component.
                // and other props, which will go to div
                //style={{ width: '200px', height: '200px' }} /// these are optional style, it is not necessary
            >

                    <FronSize className="flip-card-front row_white" onClick={this.showBack}>
                        <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name bg-white"}>{this.props.name}</div>
                        <div className={"prof1"}>{this.props.prof1}</div>
                        <div className={"prof2"}>{this.props.prof2}</div>
                        <div className={"body"}>{this.props.body}</div>

                    </FronSize>



                    <div className="flip-card-back" onClick={this.showFront}>
                        <button onClick={this.handleClick}>Flip Card</button>
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>

                    </div>

            </Flippy>
        )
    }


}

export default ReactCardClip;
