import React, {Component, useState} from "react";
import {Link, animateScroll as scroll } from "react-scroll";
import {Navbar, Nav} from "react-bootstrap";
import {translate} from "react-switch-lang"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";
import logo from "./logoKancelar.png"

//const [expanded, setExpanded] = useState(false);



class Headers extends Component {

    constructor(props) {
        super(props);
        this.toggle = this.toggle.bind(this);
        this.closeNavbar = this.closeNavbar.bind(this);
        this.handleClickOutside = this.handleClickOutside.bind(this);
        this.state = {
            isOpen: false
        };
    }

    closeNavbar() {
        console.log('TEST')
        this.setState({
            isOpen: false
        });
    }


    scrollToTop = () => {
        scroll.scrollToTop();
    };

    componentWillMount() {
        document.addEventListener('mousedown', this.handleClickOutside);
    }
    componentWillUnmount() {
        document.removeEventListener('mousedown', this.handleClickOutside);
    }

    toggle() {
        this.setState({
            isOpen: !this.state.isOpen
        });
    }
    closeNavbar() {
        this.setState({
            isOpen: false
        });
    }
    handleClickOutside(event) {
        const t = event.target;
        if (this.state.isOpen && !t.classList.contains('navbar-toggler')) {
            this.closeNavbar();
        }
    }

    render() {

        //const mystyle = {
       //     color: "white",
       //     bordercolor:"white",
       //     backgroundcolor: "wgite"
      //  }

        const {t} = this.props;
        //collapseOnSelect
        //navExpanded={this.state.navExpanded}
           return (
               <Navbar  sticky="top"
                        expand="md"
                       className="nav fixed-top"  bg="black" variant="black">
                   <Navbar.Brand href="#home"><img width="47" height="39"
                                                   src={logo}
                                                   alt="Logo"
                                                   onClick={this.scrollToTop} />
                   </Navbar.Brand>

                   <Navbar.Toggle onClick={this.toggle} aria-controls="responcive-navbar-nav"/>
                   <Navbar.Toggle onClick={this.toggle} aria-controls="responcive-navbar-nav"/>

                   <Navbar.Collapse className="navbar" id="responcive-navbar-nav" isOpen={this.state.isOpen}>
                       <Nav className="mr-auto">
                           <Link className="nav-item" onClick={this.closeNavbar}
                                 activeClass="msactive"
                                 to="slayder"
                                 spy={true}
                                 href="#slayder"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section1.title')}</Link>
                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="about"
                                 spy={true}
                                 href="#about"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section2.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="services"
                                 spy={true}
                                 href="#services"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section3.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="practica"
                                 spy={true}
                                 href="#practica"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section4.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="team"
                                 spy={true}
                                 href="#team"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section5.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="pravila"
                                 spy={true}
                                 href="#pravila"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section7.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="kontact"
                                 spy={true}
                                 href="#kontact"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section6.title')}</Link>

                       </Nav>

                   </Navbar.Collapse>
               </Navbar>

           )
    }
}

export default translate(Headers);

