import React, {Component} from "react";
import {Link, animateScroll as scroll } from "react-scroll";
import {Navbar, Nav, Container, NavDropdown, Button} from "react-bootstrap";
import {translate} from "react-switch-lang"
import logo from "./logoKancelar.png"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";


class Headers extends Component {
    scrollToTop = () => {
        scroll.scrollToTop();
    };


    render() {
        const mystyle = {
            color: "white",
            bordercolor:"white",
            backgroundcolor: "wgite"
        }
        const newDrop = true;

        const {t} = this.props;
           return (
                <Navbar sticky="top" collapseOnSelect expand="md" className="nav fixed-top"  bg="black" variant="black">
                <Container fluid={true}>
                <Navbar.Brand href="#home"><img width="47" height="39"
                                                src={logo}
                                                alt="Logo"
                                                onClick={this.scrollToTop}
                /></Navbar.Brand>

                <Navbar.Toggle aria-controls="responcive-navbar-nav"/>
                <Navbar.Collapse style={mystyle} className="navbar"
                                 id="responcive-navbar-nav">
                    <Nav className="mr-auto">
                        <Nav.Link><Link className="nav-item"
                                activeClass="msactive"
                                to="slayder"
                                spy={true}
                                href="#slayder"
                                smooth={true}
                                offset={-50}
                                duration={800}
                            >{t('header.section1.title')}</Link></Nav.Link>
                        <Nav.Link> <Link className="nav-item"
                            activeClass="msactive"
                            to="about"
                            spy={true}
                            href="#about"
                            smooth={true}
                            offset={-50}
                            duration={800}
                        >{t('header.section2.title')}</Link></Nav.Link>
                        <Nav.Link><Link className="nav-item"
                            activeClass="msactive"
                            to="services"
                            spy={true}
                            href="#services"
                            smooth={true}
                            offset={-50}
                            duration={800}
                        >{t('header.section3.title')}</Link></Nav.Link>

                        <Nav.Link hidden={true}><Link className="nav-item"
                            activeClass="msactive"
                            to="practica"
                            spy={true}
                            href="#practica"
                            smooth={true}
                            offset={-50}
                            duration={800}
                        >{t('header.section4.title')}</Link></Nav.Link>

                        <Nav.Link><Link className="nav-item"
                            activeClass="msactive"
                            to="team"
                            spy={true}
                            href="#team"
                            smooth={true}
                            offset={-50}
                            duration={800}
                        >{t('header.section5.title')}</Link></Nav.Link>

                        <Nav.Link><Link className="nav-item"
                              activeClass="msactive"
                              to="pravila"
                              spy={true}
                              href="#pravila"
                              smooth={true}
                              offset={-50}
                              duration={800}
                        >{t('header.section7.title')}</Link></Nav.Link>

                        <Nav.Link><Link className="nav-item"
                              activeClass="msactive"
                              to="kontact"
                              spy={true}
                              href="#kontact"
                              smooth={true}
                              offset={-50}
                              duration={800}
                        >{t('header.section6.title')}</Link></Nav.Link>

                    </Nav>
                    <Nav>
                        <Button hidden={true} style={{background:'darkgoldenrod', color:'white', border:'none'}} className={'btn btn-sm'}>Enter</Button>
                    </Nav>

                </Navbar.Collapse>
                </Container>
            </Navbar>

           )
    }
}


export default translate(Headers);

