import React, {Component} from "react"
import {Container} from "react-bootstrap";
import image1 from './Sekcja1.jpg'
import image2 from './Sekcja2.jpg'
import image3 from './Sekcja3.jpg'
import './Slayder.css'
import BackgroundSlider from '../../BackgroundSlider/BackgroundSlider'

//'react-background-slideshow'//'react-background-slider'

class Slayder extends Component {
    render (){
        //let intervalCsaHead = setInterval(csaHead,8000);
        return (
            <Container  fluid={true}  className={'row_brown'}>


                <Container  fluid={true}  className={'slayder'}>
                    <BackgroundSlider
                                      images={[image1, image2, image3]}
                                      duration={3}
                                      transition={2}
                    />
                    <div className={'wrapper ml-5'}

                    >
                        <div className={'item1'} style={{color:'white'}}></div>
                        <div className={'item3'}>
                            Zgubiłeś szlak prowadzący do celu?
                            <br />Nie wiesz jak się poruszać po ścieżkach ustaw?
                            <br/>Szukasz drogowskazu do bezpiecznych prawnie rozwiązań?
                            <div className={'item2'}>
                                <h2 className="elementor-heading-title elementor-size-default">Naszym powołaniem jest być Twoim
                                    przewodnikiem.</h2>
                            </div>
                            <div className={'item1'}>
                                <button type="button" className="mt-1 btn btn-outline-light"><a className={"text-white"} href="#kontact">
                                    SKONTAKTUJ SIĘ Z NAMI</a></button>

                            </div>
                        </div>
                    </div>
            </Container>

            </Container>

        )
    }
}

