import React, {Component} from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import {Navbar, Nav, Container} from "react-bootstrap";
import {translate} from "react-switch-lang"
import logo from "./logoKancelar.png"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";


class Headers extends Component {
    scrollToTop = () => {
        scroll.scrollToTop();
    };

    render() {
    const {t} = this.props;
           return (
                <Navbar sticky="top" collapseOnSelect expand="md" className="nav navbar-expand-lg fixed-top scrolling-navbar bg-black" >
                <Container>
                <Navbar.Brand href="#home"><img width="40" height="39"
                                                src={logo}
                                                alt="Logo"
                                                onClick={this.scrollToTop}
                /></Navbar.Brand>

                <Navbar.Toggle aria-controls="navbar-nav"/>

                <Navbar.Collapse id="navbar-nav">
                    <Nav className="mr-auto">

                        <Nav.Link className="nav-link"><Link className="nav-item"
                                activeClass="active"
                                to="section1"
                                spy={true}
                                href="#section1"
                                smooth={true}
                                offset={-70}
                                duration={800}
                            >{t('header.section1.title')}</Link></Nav.Link>
                        <Nav.Link className="nav-link"><Link className="nav-item"
                            activeClass="active"
                            to="section2"
                            spy={true}
                            href="#section2"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section2.title')}</Link></Nav.Link>
                        <Nav.Link className="nav-link"><Link className="nav-item"
                            activeClass="active"
                            to="section3"
                            spy={true}
                            href="#section3"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section3.title')}</Link></Nav.Link>
                        <Nav.Link className="nav-link"><Link className="nav-item"
                            activeClass="active"
                            to="section4"
                            spy={true}
                            href="#section4"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section4.title')}</Link></Nav.Link>
                        <Nav.Link className="nav-link"><Link className="nav-item"
                            activeClass="active"
                            to="section5"
                            spy={true}
                            href="#section5"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section5.title')}</Link></Nav.Link>

                    </Nav>
                </Navbar.Collapse>
                </Container>
            </Navbar>

           )
    }
}


export default translate(Headers);

