import React from 'react'
import PropTypes from 'prop-types'
import generateStyleSheet from './generateStyleSheet'
import injectCss from './injectCss'
import {Container} from "react-bootstrap";

const NAME = 'ReactBackgroundSlider'

function BackgroundSlider ({
  images,
  duration,
  transition
}) {
  React.useEffect(() => {
    injectCss(
      generateStyleSheet({
        imagesCount: images.length,
        duration,
        transition
      }),
      NAME
    )
  })

  return (
    <div id={NAME}>
      {images.map((img, key) =>
        <figure key={key}
          style={{
            backgroundImage: `url(${img})`,
            animationDelay: `${(duration + transition) * key}s`
          }}
        >
          <div className={"card-text mt-5 ml-5"}>
            <p>Nie wiesz jak się poruszać po ścieżkach ustaw?</p>
            <p>Szukasz drogowskazu do bezpiecznych prawnie rozwiązań?</p>
            <p>Naszym powołaniem jest być twoim przewodnikiem.</p>
          </div>

        </figure>


        )}
    </div>
  )
}

BackgroundSlider.defaultProps = {
  duration: 10,
  transition: 2
}

BackgroundSlider.propTypes = {
  images: PropTypes.array.isRequired,
  duration: PropTypes.number,
  transition: PropTypes.number
}

export default BackgroundSlider
