import React, {Component} from "react";
import "./ReactCardClip.css"
import ReactCardFlip from 'react-card-flip';

class ReactCardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            active: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: '',
            style:''
        };
        this.toggleClass = this.toggleClass.bind(this);
    }

    render() {
        return(
            <div ref="flip-card" className={'flip-card'} >
                <div className={this.state.active ? 'flip-card-inner' : 'flip-card-inner'} style={{style}} onClick={this.toggleClass}>
                    <div className="flip-card-front row_white">
                        <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name bg-white"}>{this.props.name}</div>
                        <div className={"prof1"}>{this.props.prof1}</div>
                        <div className={"prof2"}>{this.props.prof2}</div>
                        <div className={"body"}>{this.props.body}</div>
                    </div>
                    <div className="flip-card-back">
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>
                    </div>
                </div>
            </div>
        )
    }


}

export default ReactCardClip;
