import React, {Component} from "react";

import "./CardClip.css"

class CardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            active: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: '',
            style:''
        };
        this.toggleClass = this.toggleClass.bind(this);
    }


    toggleClass() {
        //this.refs.flip-card-inner.classList.toogle("meFlip");
        const currentState = this.state.active;
        this.setState({ active: !currentState });
        console.log(document.getElementsByClassName("flip-card-inner"))
        //let contentElement = this.state.getElementById('flip-card-inner')
        //console.log(contentElement)
        if (!this.state.active) {
            this.setState({style: "transform: rotateY(180deg)"})
        } else
        {
            //this.toggleClass('flip-card-inner').
            //this.state.getElementById('flip-card-inner').style.removeProperty('transform')

            //document.getElementById("myDIV").style.transform = "rotate(7deg)";

            this.setState({style:null})
        }
    };


    render() {

        function flip(event){
            let element = event.currentTarget;
            if (element.className === "flip-card-inner") {
                if(element.style.transform === "rotateY(180deg)") {
                    element.style.transform = "rotateY(0deg)";
                }
                else {
                    element.style.transform = "rotateY(180deg)";
                }
            }
        };

        //
        let style = this.state.style
        console.log(style)
        //className={"flip-card-inner"}
        //className={this.state.active ? 'flip-card-inner flip-card-back': 'flip-card-inner flip-card-front'



        //let style = "transform: 'rotateY(180deg)'"
        //style={{transform: 'rotateY(180deg)'}} >

        //<div className={this.state.active ? 'flip-card  flip-card-inner' : 'flip-card-inner'}>
        //onClick={this.toggleClass}
        return (
            <div className={'flip-card'} >
                <div className={'flip-card-inner'} onClick={flip}>
                        <div className="flip-card-front row_white">
                            <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                            <div className={"name bg-white"}>{this.props.name}</div>
                            <div className={"prof1"}>{this.props.prof1}</div>
                            <div className={"prof2"}>{this.props.prof2}</div>
                            <div className={"body"}>{this.props.body}</div>
                        </div>
                        <div className="flip-card-back">
                            <div className={"backTitle"}>{this.props.backTitle}</div>
                            <div className={"backText"}>{this.props.backText}</div>
                        </div>
                    </div>
            </div>
        )
    }
}

export default CardClip
