import React, {Component} from "react";
import {Link, animateScroll as scroll } from "react-scroll";
//import {Navbar, Nav, NavDropdown, Button} from "react-bootstrap";
import {translate} from "react-switch-lang"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";
import { createStyles, makeStyles, Theme, createMuiTheme } from '@material-ui/core/styles';
import {AppBar, Toolbar, IconButton, Menu as MenuIcon, Typography, Button} from "@material-ui/core";

import logo from "./logoKancelar.png"

class Headers extends Component {

    theme = createMuiTheme({
        palette: {
            primary: {
                main: '#11cb5f'
            }
        }
    });

    useStyles = makeStyles((theme: Theme) =>
        createStyles({
            root: {
                flexGrow: 1,
            },
            menuButton: {
                marginRight: theme.spacing(2),
            },
            title: {
                flexGrow: 1,
            },
        }),
    );


    scrollToTop = () => {
        scroll.scrollToTop();
    };


    render() {


        //const mystyle = {
       //     color: "white",
       //     bordercolor:"white",
       //     backgroundcolor: "wgite"
      //  }



        const {t} = this.props;
           return (

               <AppBar position="fixed">
                   <Toolbar>
                       <IconButton edge="start" className={this.useStyles.root} color="inherit" aria-label="menu">
                           <MenuIcon />
                       </IconButton>
                       <Typography variant="h6" className={this.useStyles.title}>
                           News
                       </Typography>
                       <Button color="inherit">Login</Button>
                   </Toolbar>
               </AppBar>

           )
    }
}

export default translate(Headers);

