import React, {Component} from "react";
import "./CardClip.css"

class CardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            active: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: ''
        };
        this.toggleClass = this.toggleClass.bind(this);
    }

    toggleClass() {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
        console.log(this.state.active)
    };


    render() {
        let flipped = this.state.active ? "flip-card-back" : "flip-card-front";
        console.log(flipped)
        //className={"flip-card-inner"}
        //className={this.state.active ? 'flip-card-inner flip-card-back': 'flip-card-inner flip-card-front'}
        //if (this.state.active){className+=}
        //style={{transform: 'rotateY(180deg)'}} >
        return (
            <div className="flip-card"  onClick={this.toggleClass}>
                <div className={"flip-card-inner"}>
                    <div className="flip-card-front row_white">
                        <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name bg-white"}>{this.props.name}</div>
                        <div className={"prof1"}>{this.props.prof1}</div>
                        <div className={"prof2"}>{this.props.prof2}</div>
                        <div className={"body"}>{this.props.body}</div>
                    </div>
                    <div className="flip-card-back">
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>
                    </div>
                </div>
            </div>
        )
    }
}

export default CardClip
