import React, {Component} from "react"
import "./Practica.css"
import {translate} from "react-switch-lang";
import {Container, Row, Col, Image} from "react-bootstrap";
import {Divider} from "@material-ui/core";
import 'bootstrap/dist/css/bootstrap.min.css';

class Practica extends Component {
    state = {
        id:"0"
    }
    render() {
        const {t} = this.props;
        return (
            <Container fluid={true} className={"practica"} id={this.props.id}>
                <div className={"pt-3"}>
                    <h1 className="text-center">{t('header.section4.title')}</h1>
                </div>
                    <Row className="p-3 row_brown">
                        <Col sm={4}>
                            <picture>
                                <Image src="./PRACTICA1.png" width="1024" height="1024" className="img-fluid"  alt="ONAS"/>
                            </picture>

                        </Col>
                        <Col sm={8}>
                            <Container className="pl-sm-5">
                                <div className="zagolov text-center">Obsługa inwestycji budowlanych </div>
                                <p className={"mt-2"}>Nic tak nie zabezpieczy interesów stron procesu inwestycyjno-budowlanego jak precyzyjnie skonstruowana umowa i rzetelny obieg dokumentów. W przeciwnym razie łatwo narazić się na zarzut niewłaściwego wykonywania umowy, co może skończyć się zerwaniem kontraktu, naliczeniem kar umownych lub odmową zapłaty wynagrodzenia. Podejmując się kompleksowej obsługi prawnej inwestycji budowlanych czuwamy, aby zawierane umowy normowały wszystkie kwestie mogące rodzić spory na przyszłość oraz należycie chroniły interesy Klienta. W toku realizacji inwestycji dbamy, aby były odpowiednio dokumentowane wszelkie podejmowane czynności oraz okoliczności wpływające na jej przebieg. Doradzamy także jak reagować w sytuacji nierzetelnego wykonywania umów przez kontrahentów i pomagamy egzekwować od nich odpowiednie roszczenia, w tym o zapłatę wynagrodzenia, kar umownych, czy usunięcie usterek.</p>

                                    <p className={"text-center"}>Dowiedz się więcej</p>
                            </Container>
                        </Col>
                    </Row>
                    <Row className="row_white">
                        <Col sm={8}>

                        </Col>
                        <Col sm={4}>
                            <picture>
                                <Image src="./PRACTICA2.png" width="1024" height="1024" className="img-fluid"  alt="ONAS"/>
                            </picture>
                        </Col>
                    </Row>
            </Container>
        )
    }
}
export default translate(Practica)
