import React, {Component} from "react"
import {Container} from "react-bootstrap";
import image1 from './Sekcja1.jpg'
import image2 from './Sekcja2.jpg'
import image3 from './Sekcja3.jpg'
//import BackgroundSlideshow from 'react-background-slideshow'

class Slayder extends Component {
 //height:'600px', width:'800px'
    render (){

     let op=1;          // Прозрачность
     let img_front=1;   // Индекс отображаемой картинки
     let img_back=2;    // Индекс фоновой картинки
     let img_back1=3;    // Индекс фоновой картинки
     const img_total=3;  // Общее количество картинок в ротации

// Функция изменения прозрачности отображаемой картинки
     function fade() {
      // Непрозрачность уменьшается
      op=op-0.1;
      if (op<0) {
       // Картинка непрозрачная
       op=1;

       // Берем следующие картинки
       this.img_front=this.img_front+1;
       this.img_back=this.img_back+1;
       // Если показали все, то переходим обратно на первую
       if (this.img_front>this.img_total) { this.img_front=1; }
       if (this.img_back>this.img_total) { this.img_back=1; }

       // Установить новые картинки на фон и отображение
       let tmp = document.getElementById('sh_front');
       // Прозрачность для браузеров
       tmp.style.opacity = op;
       // Прозрачность для IE
       tmp.style.filter='progid:DXImageTransform.Microsoft.Alpha(opacity=100)';
       tmp.src='img/'+img_front+'.jpg';
       let tmp_back = document.getElementById('sh_back');
       tmp_back.src='img/'+img_back+'.jpg';

       // Пауза между сменой картинок в 5 секунд
       setTimeout('waiting()',5000);
      }
      else {
       // Установить новое значение прозрачности для верхней картинки
       //document.getElementById('sh_front').style.opacity=op;
       // Прозрачность для браузеров
       //tmp.style.opacity=op;
       // Прозрачность для IE
       //document.getElementById('sh_front').style.filter='progid:DXImageTransform.Microsoft.Alpha(opacity='+
         //  Math.round(op*100)+')';
       //tmp

       // Следующая итерация цикла затухания
       setTimeout('fade()',50);
      }
     }

// Функция запуска эффекта затухания
     function waiting() {
      fade();
     }
        return (
        <Container  fluid={true} id={"slayder"} onLoad={waiting()}>
          <img style={{position: 'absolute', top:'0px'}} id='sh_back' src={image1} alt={"image1"}/>
          <img style={{position: 'absolute', top:'0px'}} id="sh_front" src={image2} alt={"image2"}/>

        </Container>

        )
    }
}

