import React, {Component} from "react";
import {Link, animateScroll as scroll } from "react-scroll";
import {Navbar, Nav} from "react-bootstrap";
import {translate} from "react-switch-lang"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";
import logo from "./logoKancelar.png"
import { LinkContainer } from "react-router-bootstrap";

//const [expanded, setExpanded] = useState(false);



class Headers extends Component {




    scrollToTop = () => {
        scroll.scrollToTop();
    };




    render() {

        //const mystyle = {
       //     color: "white",
       //     bordercolor:"white",
       //     backgroundcolor: "wgite"
      //  }

        const {t} = this.props;
        //collapseOnSelect
        //navExpanded={this.state.navExpanded}
           return (
               <Navbar  sticky="top"
                        expand="md"
                       className="nav fixed-top"  bg="black" variant="black"
                    collapseOnSelect={true}
               >
                   <Navbar.Brand href="#home"><img width="47" height="39"
                                                   src={logo}
                                                   alt="Logo"
                                                   onClick={this.scrollToTop} />
                   </Navbar.Brand>

                   <Navbar.Toggle  aria-controls="responcive-navbar-nav"/>

                   <Navbar.Collapse className="navbar" id="responcive-navbar-nav">
                       <Nav className="mr-auto">
                           <LinkContainer to="slayder" href="#slayder">

                               <Link className="nav-item"
                                 activeClass="msactive"
                                 to="slayder"
                                 spy={true}
                                 href="#slayder"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section1.title')}</Link></LinkContainer>
                           <Link className="nav-item" onClick={this.closeNavbar}
                                 activeClass="msactive"
                                 to="about"
                                 spy={true}
                                 href="#about"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section2.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="services"
                                 spy={true}
                                 href="#services"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section3.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="practica"
                                 spy={true}
                                 href="#practica"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section4.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="team"
                                 spy={true}
                                 href="#team"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section5.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="pravila"
                                 spy={true}
                                 href="#pravila"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section7.title')}</Link>

                           <Link className="nav-item"
                                 activeClass="msactive"
                                 to="kontact"
                                 spy={true}
                                 href="#kontact"
                                 smooth={true}
                                 offset={-50}
                                 duration={800}
                           >{t('header.section6.title')}</Link>

                       </Nav>

                   </Navbar.Collapse>
               </Navbar>

           )
    }
}

export default translate(Headers);

