import React, {Component, useEffect, useState} from "react"
import "./Kontact.css"
import is from 'is_js';
import {translate} from "react-switch-lang";
import {Container, Row, Col, Form, Button} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Icon } from '@iconify/react';
import mapMarkedAlt from '@iconify/icons-fa-solid/map-marker-alt';
import emailjs from 'emailjs-com';

//User ID: user_Fae8JGudb10fQXLUaSFBd
//Access token: c91896dcdecf2987da6325db963daac3
//http://kancelaria-burkiewicz-sliwa.pl

class Kontact extends Component {

    constructor(props) {
        super(props);
        this.state = {
            id: "0",
            user_name:'',
            user_email:'',
            contact_number:'',
            message: '',
            count: 0,
            btnEnabled:false,
            validation: {
                maxLenght:500

            }
        };

        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleEmailChange = this.handleEmailChange.bind(this);
        this.handlePhoneChange = this.handlePhoneChange.bind(this);
        this.handleTextareaChange = this.handleTextareaChange.bind(this);

    }
    validateControl = (value, validation) => {

        let isValid = false

        if (validation==='user_email'){

            isValid = is.email(value) &&isValid
        }

        if (validation === 'message'){
            isValid = value.length >= 10
        }
        return isValid;
    }
    onChangeHandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        let btnEnabled = false;

        if (this.validateControl(value, name)){
            btnEnabled=true;
        }
        this.setState({
            btnEnabled
        })
    }


    handleNameChange(event) {
        this.onChangeHandler(event);
        this.setState({user_name: event.target.value});
    }

    handlePhoneChange(event){
        this.setState({contact_number: event.target.value});
    }

    handleEmailChange(event) {
        this.onChangeHandler(event);
        this.setState({user_email: event.target.value});
    }

    handleTextareaChange(event ) {
        this.onChangeHandler(event, 'message');
        this.setState({message: event.target.value, count: event.target.value.length});
    }

    render() {
        let state = this.state;
        let isValid = this.state.btnEnabled

        function networkRequest() {
            console.log(state)
            return new Promise((resolve) => {//setTimeout(resolve, 2000));

               console.log(state)
            const template_params1 = {
                "reply_to": state.user_email,
                "from_name": state.user_name,
                "contact_number": state.contact_number,
                "to_name": "kancelatiya",
                "message_html": state.message
            }

            const service_id = "default_service";
            const template_id = "template_U1BSvr3p";
            const iser_id ="user_Fae8JGudb10fQXLUaSFBd";

            emailjs.send(service_id, template_id, template_params1, iser_id)
                .then((result) => {
                    //console.log(window)
                    setTimeout(resolve, 100);
                    //console.log(result.text);
                }, (error) => {
                    console.log(error.text);
                    setTimeout(resolve, 100);
                });
        });

        }

        function LoadingButton() {
            const [isLoading, setLoading] = useState(false);

            useEffect(() => {
                if (isLoading) {
                    networkRequest().then(() => {
                        setLoading(false);
                    });
                }
            }, [isLoading]);

            const handleClick = () => setLoading(true);

            return (
                <div>
                    <div className={"text-white"}>{isValid}</div>
                    <div className={"text-white"}>{isLoading}</div>
                <Button
                    variant="primary"
                    disabled={isLoading || !isValid}
                    onClick={!isLoading  ? handleClick : null}
                >
                    {isLoading ? 'Wysyłka…' : ' Wyślij wiadomość'}
                </Button>
                </div>
            );
        }

        const {t} = this.props;
        return (

            <Container fluid={true} className={"kontact"}>
            <Container fluid={'sm'} className={"kontact"} id={this.props.id}>
                <div className={"pt-3"}>
                    <h1 className="text-center heading-title">{t('header.section6.title')}</h1>
                </div>
                    <Row className="p-3 row_brown">
                        <Col sm={6}>
                            <Form onSubmit={this.handleSubmit}>
                                <Form.Group className={"mb-3"} controlId="formBasicEmail">
                                    <Form.Control className={"mb-3"} name="user_name" type="text" placeholder="Imię" value={this.state.name} onChange={this.handleNameChange}/>
                                    <Form.Control className={"mb-3"} name="user_email" type="email" placeholder="Enter email" value={this.state.email} onChange={this.handleEmailChange} />
                                    <Form.Control className={"mb-3"} name="contact_number" type="text" placeholder="Telefon"  value={this.state.phone} onChange={this.handlePhoneChange}/>
                                    <Form.Control  maxLength={this.state.validation.maxLenght} name="message" as="textarea" rows="5" placeholder="Treść wiadomości" value={this.state.textarea} onChange={this.handleTextareaChange}/>
                                    <small>{this.state.count} z 500</small>
                                </Form.Group>
                                <LoadingButton />
                            </Form>

                        </Col>
                        <Col sm={6} className={"justify-content-center"}>
                            <div style={{background: 'darkgoldenrod', boxSizing: 'border-box'}}>
                            <h3 className={"text-center font-weight-bold pt-3"}>Gdzie nas znajdziesz</h3>
                            <hr />
                            <Row className="justify-content-md-center pb-4">

                                <Col xs sm={4} className="text-center m-0 p-0">
                                    <span style={{fontSize: '80px', color: 'floralwhite'}}>
                                        <Icon icon={mapMarkedAlt} />

                                    </span>
                                </Col>
                                <Col xs sm={8}>
                                    <span style={{color: "white"}}>ul. Topolowa 31/1    31-506 Kraków</span><br />
                                    <span style={{fontSize: "15px"}}>tel. 12 312 53 22<br />
                                    sekretariat@kancelaria-b-s.pl<br />
                                    Pon-Pt - 08:00-19:00</span>

                                </Col>
                            </Row>
                            </div>
                        </Col>
                    </Row>
                    <Row>
                        <iframe title="MapKancelaria"
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2561.063880815331!2d19.95367544405478!3d50.0663652887421!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x47165b185f36d53b%3A0x96d15ce836ab9dd3!2sTopolowa%2031%2C%2031-506%20Krak%C3%B3w!5e0!3m2!1sru!2spl!4v1590245269460!5m2!1sru!2spl"
                            width="100%" height="300px" frameBorder="0" style={{border:0}} allowFullScreen=""
                            aria-hidden="false" tabIndex="0"></iframe>
                    </Row>
            </Container>
            </Container>
        )
    }

}

export default translate(Kontact)
