import React, {Component} from "react";
import "./ReactCardFlip.css"
import FlipCard from 'react-flipcard';

class ReactCardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            isFlipped: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: '',
            style:''
        };
        this.handleClick = this.handleClick.bind(this);
    }

    getInitialState() {
        return {
            isFlipped: false
        };
    }
    showBack() {
        this.setState({
            isFlipped: true
        });
    }

    showFront() {
        this.setState({
            isFlipped: false
        });
    }

    handleKeyDown(e) {
        if (this.state.isFlipped && e.keyCode === 27) {
            this.showFront();
        }
    }

    handleOnFlip(flipped) {
        if (flipped) {
            this.refs.backButton.getDOMNode().focus();
        }
    }


    render() {

        return(
            <FlipCard
                disabled={true}
                flipped={this.state.isFlipped}
                onFlip={this.handleOnFlip}
                onKeyDown={this.handleKeyDown}
            >

                    <div className="flip-card-front row_white" onClick={this.showBack}>
                        <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name bg-white"}>{this.props.name}</div>
                        <div className={"prof1"}>{this.props.prof1}</div>
                        <div className={"prof2"}>{this.props.prof2}</div>
                        <div className={"body"}>{this.props.body}</div>

                    </div>



                    <div className="flip-card-back" onClick={this.showFront}>
                        <button onClick={this.handleClick}>Flip Card</button>
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>

                    </div>

            </FlipCard>
        )
    }


}

export default ReactCardClip;
