import React, {Component} from "react"
import "./Team.css"
import {translate} from "react-switch-lang";
import {Container, Row, Col, Card, CardDeck} from "react-bootstrap";
import 'bootstrap/dist/css/bootstrap.min.css';
import Flippy, { FrontSide, BackSide } from 'react-flippy';


class Team extends Component {

    constructor() {
        super();
        this.state = {
            id:"0"
        };

    }
/*
*     getInitialState() {
        return {
            isFlipped: false
        };
    }

    showBack() {
        this.setState({
            isFlipped: true
        });
    }

    showFront() {
        this.setState({
            isFlipped: false
        });
    }

    handleOnFlip(flipped) {
        if (flipped) {
            this.refs.backButton.focus();
        }
    }


    handleKeyDown(e) {
        if (this.state.isFlipped && e.keyCode === 27) {
            this.showFront();
        }
    }
*/

    render() {
        const {t} = this.props;
        return (

            <Container fluid={false} className={"team"} id={this.props.id}>
                <div className={"pt-3"}>
                    <h1 className="text-center">{t('header.section5.title')}</h1>
                </div>
                    <Row className={"mb-5 justify-content-md-center"}>
                    <Col sm={2} xs={1}></Col>
                    <Col sm={8} xs={10}>
                    <CardDeck>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src="Zespol-ja-1.jpg" width="50em" className="img-fluid rounded"  alt="Zespol ja"/>
                            <Card.Body>
                                <Card.Title className="text-center font-weight-bold">Bernadetta Burkiewicz-Śliwa</Card.Title>
                                <Card.Text className="text-center">
                                    adwokat - właściciel kancelarii
                                </Card.Text>
                            </Card.Body>
                        </Card>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src="Zespol-Kuba-1.jpg" width="50px" className="img-fluid rounded"  alt="Zespol kuba"/>
                            <Card.Body>
                                <Card.Title className="text-center font-weight-bold">Jakub Kuś</Card.Title>
                                <Card.Text className="text-center ">
                                    aplikant adwokacki - partner kancelarii
                                </Card.Text>
                            </Card.Body>
                        </Card>
                    </CardDeck>
                    </Col>
                    <Col sm={2} xs={1}></Col>
                    </Row>

                <Row className={"text-center mb-5 justify-content-md-center"}>
                    <Col sm={2} xs={1}></Col>
                    <Col sm={8} xs={10}>
                    <CardDeck>
                        <Flippy
                            flipOnHover={true} // default false
                            flipOnClick={true} // default false
                            flipDirection="horizontal" // horizontal or vertical
                            ref={(r) => this.flippy = r} // to use toggle method like this.flippy.toggle()
                            // if you pass isFlipped prop component will be controlled component.
                            // and other props, which will go to div
                            //style={{ width: '200px', height: '200px' }} /// these are optional style, it is not necessary
                        >
                        <FrontSide>
                        <Card >
                            <Card.Img variant="top" src="DSC09488-1.jpg" className="img-fluid rounded"  alt="DSC09488"/>
                            <Card.Body>
                                <Card.Title className="text-center font-weight-bold">Alicja Burkiewicz</Card.Title>
                                <Card.Text className="text-center">Kierownik biura
                                    aplikant adwokacki - partner kancelarii
                                </Card.Text>

                            </Card.Body>
                            <Card.Footer>Obsługa i zarządzanie sprawami administracyjnymi i finansowymi, asystent ds. dotacji oraz ds. wykroczeń</Card.Footer>
                        </Card>
                        </FrontSide>

                            <BackSide>
                                TEKST
                            </BackSide>
                        </Flippy>

                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src="DSC01274-683x1024.jpg" width="50px" className="img-fluid rounded"  alt="DSC09488"/>
                            <Card.Body>
                                <Card.Title className="text-center font-weight-bold">Elisaveta Niehanova</Card.Title>
                                <Card.Text className="text-center">asystent ds. administracyjno-prawnych oraz ds. dotacji
                                </Card.Text>
                            </Card.Body>
                            <Card.Footer>ds. dotacji</Card.Footer>
                        </Card>


                    </CardDeck>
                    </Col>
                    <Col sm={2} xs={1}></Col>
                </Row>
            </Container>
        )
    }
}
export default translate(Team)
