import React, {Component} from "react";
import { Link, animateScroll as scroll } from "react-scroll";
import {Navbar, Nav, Container, NavDropdown, Form} from "react-bootstrap";
import {translate, getLanguage, setLanguage} from "react-switch-lang"
import logo from "./logoKancelar.png"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";


class Headers extends Component {
    scrollToTop = () => {
        scroll.scrollToTop();
    };

    handleSetLanguage = (key) => () => {
        setLanguage(key);
    };

    render() {
        const mystyle = {
            color: "white",
            bordercolor:"white",
            backgroundcolor: "white"
        }
    const {t} = this.props;
           return (
                <Navbar sticky="top" collapseOnSelect expand="md" className="nav fixed-top" >
                <Container fluid={true}>
                <Navbar.Brand href="#home"><img width="40" height="39"
                                                src={logo}
                                                alt="Logo"
                                                onClick={this.scrollToTop}
                /></Navbar.Brand>

                <Navbar.Toggle aria-controls="responcive-navbar-nav"/>
                <Navbar.Collapse style={mystyle} className="navbar"
                                 id="responcive-navbar-nav">
                    <Nav className="mr-auto">
                        <Link className="nav-item"
                                activeClass="msactive"
                                to="home"
                                spy={true}
                                href="#home"
                                smooth={true}
                                offset={-70}
                                duration={800}
                            >{t('header.section1.title')}</Link>
                        <Link className="nav-item"
                            activeClass="msactive"
                            to="about"
                            spy={true}
                            href="#about"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section2.title')}</Link>
                        <Link className="nav-item"
                            activeClass="msactive"
                            to="services"
                            spy={true}
                            href="#services"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section3.title')}</Link>
                        <Link className="nav-item"
                            activeClass="msactive"
                            to="practica"
                            spy={true}
                            href="#practica"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section4.title')}</Link>
                        <Link className="nav-item"
                            activeClass="msactive"
                            to="team"
                            spy={true}
                            href="#team"
                            smooth={true}
                            offset={-70}
                            duration={800}
                        >{t('header.section5.title')}</Link>
                        <Link className="nav-item"
                              activeClass="msactive"
                              to="kontact"
                              spy={true}
                              href="#kontact"
                              smooth={true}
                              offset={-70}
                              duration={800}
                        >{t('header.section6.title')}</Link>                    </Nav>
                    <Form inline>
                        <NavDropdown className={"dropdown"} title={getLanguage().toUpperCase()} id="lng-dropdown">
                            <NavDropdown.Item id="lng_pl" className="dropdown nav-item" href="#" onClick={this.handleSetLanguage('pl')}>PL</NavDropdown.Item>
                            <NavDropdown.Item id="lng_en" className="dropdown nav-item" href="#" onClick={this.handleSetLanguage('en')}>EN</NavDropdown.Item>
                        </NavDropdown>
                    </Form>
                </Navbar.Collapse>
                </Container>
            </Navbar>

           )
    }
}


export default translate(Headers);

