import React, {Component} from "react"
import {Container, Carousel} from "react-bootstrap";
import image1 from './Sekcja1.jpg'
import image2 from './Sekcja2.jpg'
import image3 from './Sekcja3.jpg'
import './Slayder.css'
import BackgroundSlider from '../../BackgroundSlider/BackgroundSlider'

//'react-background-slideshow'//'react-background-slider'

class Slayder extends Component {
    render (){

      return(


            <Carousel  fade={true} controls={false} indicators={false} pause={false} wrap={false} interval={6000} >
            <Carousel.Item style={{transition: 'transform 2s ease, opacity 3s ease-out'}} >
            <img
              className="d-block w-100"
              src={image1}
              alt="First slide"
            />
            <Carousel.Caption className='captionCarusel'>
            <div className={"card-text m-0 p-0"} style={{color:'white'}}>
                  <p>Zgubiłeś szlak prowadzący do celu?
                    <br />Nie wiesz jak się poruszać po ścieżkach ustaw?
                    <br/>Szukasz drogowskazu do bezpiecznych prawnie rozwiązań?
                  </p>

                  <h2 className="elementor-heading-title elementor-size-default">Naszym powołaniem jest być Twoim
                    przewodnikiem.</h2>
                  <button type="button" className="mt-1 btn btn-outline-light"><a className={"text-white"} href="#kontact">
                    SKONTAKTUJ SIĘ Z NAMI</a></button>
                </div>
            </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item style={{transition: 'transform 2s ease, opacity 3s ease-out'}}> 
            <img
              className="d-block w-100"
              src={image2}
              alt="Third slide"
            />
            <Carousel.Caption className='captionCarusel'>
              <div></div>
            </Carousel.Caption>
            </Carousel.Item>

            <Carousel.Item style={{transition: 'transform 2s ease, opacity 3s ease-out'}}>
            <img
              className="d-block w-100"
              src={image3}
              alt="Third slide"
            />
            <Carousel.Caption className='captionCarusel'>

              <div className={"card-text m-0 p-0"} style={{color:'white'}}>
                  <p>Zgubiłeś szlak prowadzący do celu?
                    <br />Nie wiesz jak się poruszać po ścieżkach ustaw?
                    <br/>Szukasz drogowskazu do bezpiecznych prawnie rozwiązań?
                  </p>

                  <h2 className="elementor-heading-title elementor-size-default">Naszym powołaniem jest być Twoim
                    przewodnikiem.</h2>
                  <button type="button" className="mt-1 btn btn-outline-light"><a className={"text-white"} href="#kontact">
                    SKONTAKTUJ SIĘ Z NAMI</a></button>
                </div>

            </Carousel.Caption>
          </Carousel.Item>
        </Carousel>
       )
        // return (
        //     <Container  fluid={true}  className={'row_brown'}>


        //         <Container  fluid={true}  className={'slayder'}>
        //             /*<BackgroundSlider
        //                               images={[image1, image2, image3]}
        //                               duration={3}
        //                               transition={2}
        //             >

        //             </BackgroundSlider>
        //     </Container>

        //     </Container>

        // )
    }
}

