import React, {Component} from "react";
import "./ReactCardFlip.css"
import ReactCardFlip from 'react-card-flip';

class ReactCardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            isFlipped: false,
            name: 'name',
            prof1: '',
            prof2: '',
            body: '',
            backTitle: '',
            backText: '',
            style:''
        };
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick(e){
        e.preventDefault();
        this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));

    }
    render() {
        return(
            <ReactCardFlip className={'flip-card'} isFlipped={this.state.isFlipped} flipDirection="horizontal">

                    <div className="flip-card-front row_white" onClick={this.handleClick}>
                        <img className={"p-4 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name bg-white"}>{this.props.name}</div>
                        <div className={"prof1"}>{this.props.prof1}</div>
                        <div className={"prof2"}>{this.props.prof2}</div>
                        <div className={"body"}>{this.props.body}</div>
                    </div>

                    <div className="flip-card-back" onClick={this.handleClick}>
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>
                    </div>
            </ReactCardFlip>
        )
    }


}

export default ReactCardClip;
