import React, {Component} from "react";
import "./CardClip.css"

class CardClip extends Component {
    constructor(props) {
        super(props);
        this.state = {
            img: '',
            active: false,
            name: 'TEST-1',
            prof: '',
            body: '',
            backTitle: '',
            backText: ''
        };
        this.toggleClass = this.toggleClass.bind(this);
    }

    toggleClass() {
        const currentState = this.state.active;
        this.setState({ active: !currentState });
        console.log(this.state.active)
    };


    render() {
        let flipped = this.state.active ? "transform : rotateY(180deg)" : "transform : rotateY(0deg)";
        return (
            <div className="flip-card"  onClick={this.toggleClass}>
                <div className="flip-card-inner" style={{flipped}}>
                    <div className="flip-card-front row_white">
                        <img className={"p-5 bg-white"} src={this.props.img} alt="Avatar" style={{width:"100%"}}/>
                        <div className={"name"}>{this.props.name}</div>
                        <div className={"prof"}>{this.props.prof}</div>
                        <div className={"body"}>{this.props.body}</div>
                    </div>
                    <div className="flip-card-back">
                        <div className={"backTitle"}>{this.props.backTitle}</div>
                        <div className={"backText"}>{this.props.backText}</div>
                    </div>
                </div>
            </div>
        )
    }
}

export default CardClip
