import React, {Component} from "react";
import {Link, animateScroll as scroll } from "react-scroll";
//import {Navbar, Nav, NavDropdown, Button} from "react-bootstrap";
import {translate} from "react-switch-lang"
import "./Header.css"
import "bootstrap/dist/css/bootstrap.min.css";
import { createStyles, makeStyles, Theme } from '@material-ui/core/styles';
import {AppBar, Toolbar, Container, IconButton, Menu as MenuIcon, Typography, Button} from "@material-ui/core";

import logo from "./logoKancelar.png"


const useStyles = makeStyles((theme: Theme) =>
    createStyles({
        root: {
            flexGrow: 1,
        },
        menuButton: {
            marginRight: theme.spacing(2),
        },
        title: {
            flexGrow: 1,
        },
    }),
);

class Headers extends Component {



    scrollToTop = () => {
        scroll.scrollToTop();
    };


    render() {


        const mystyle = {
            color: "white",
            bordercolor:"white",
            backgroundcolor: "wgite"
        }

        const classes = useStyles();

        const {t} = this.props;
           return (

               <AppBar position="fixed">
                   <Toolbar>
                       <IconButton edge="start" className={classes.root} color="inherit" aria-label="menu">
                           <MenuIcon />
                       </IconButton>
                       <Typography variant="h6" className={classes.title}>
                           News
                       </Typography>
                       <Button color="inherit">Login</Button>
                   </Toolbar>
               </AppBar>

           )
    }
}

export default translate(Headers);

